import React, { useRef, useState } from 'react';
import { useFrame } from '@react-three/fiber';
import { Sphere, useTexture } from '@react-three/drei';
import earthTexture from "./8081_earth.jpg";

export const Earth = () => {
  const earthRef = useRef();
  const texture = useTexture(earthTexture);
  const [isDragging, setDragging] = useState(false);

  // Rotar la Tierra usando useFrame
  useFrame(() => {
    if (earthRef.current) {
      earthRef.current.rotation.y += 0.005;
    }
  });

  return (
    <Sphere ref={earthRef}
    args={[2, 64, 64]}
    onClick={() => setDragging(!isDragging)}
      onPointerOver={() => setDragging(true)}
      onPointerOut={() => setDragging(false)}
      onPointerMissed={() => setDragging(false)}
      onPointerMove={(event) => {
        if (isDragging) {
          const { movementX, movementY } = event.nativeEvent;
          earthRef.current.rotation.y += movementX / 300;
          earthRef.current.rotation.x += movementY / 300;
        }
      }}
    >
      <meshStandardMaterial attach="material" map={texture} />
    </Sphere>
  );
};

export default Earth;
