// Logo.js
import React, { useRef } from 'react';
import { useFrame } from '@react-three/fiber';
import { Text } from '@react-three/drei';
import '../style/Logo.css';

const Logo = () => {
  const divingRef = useRef();
  const esRef = useRef();

  // Animación para mover hacia arriba
  useFrame(() => {
    const targetY = 3;
    const speed = 0.01;

    if (divingRef.current.position.y < targetY) {
      divingRef.current.position.y += speed;
    }

    if (esRef.current.position.y < targetY) {
      esRef.current.position.y += speed;
    }
  });

  return (
    <group>
      <Text
        ref={divingRef}
        position={[-0.5, 0, 0]}
        fontSize={1}
        color="white"
        anchorX="center"
        anchorY="middle"
        letterSpacing={-0.05}
        className="logo-text white-text"
        font="ethnocentric"
      >
        Diving
      </Text>
      <Text
        ref={esRef}
        position={[1.3, 0, 0]} // Ajusta la posición según tus necesidades
        fontSize={1}
        color="blue"
        anchorX="center"
        anchorY="middle"
        letterSpacing={-0.05}
        className="logo-text blue-text"
        font='ethnocentric'
      >ES
      </Text>
    </group>
  );
};

export default Logo;
