// App.js
import './style/App.css';
import React, { useState } from 'react';
import { Canvas } from '@react-three/fiber';
import { OrbitControls } from '@react-three/drei';
import Earth from './components/Earth';
import NewWindowComponent from './components/NewWindowComponent'; // Importa el nuevo componente

const App = () => {
  const [clicked, setClicked] = useState(false);
  const [showNewWindow, setShowNewWindow] = useState(false);

  const handleClick = () => {
    setClicked(true);
    setShowNewWindow(true);
  };

  return (
    <div className={`app-container ${clicked ? 'clicked' : ''}`} onClick={handleClick}>
      {showNewWindow ? (
        <NewWindowComponent />
      ) : (
        <Canvas camera={{ position: [0, 0, 2] }}>
          <ambientLight />
          <pointLight position={[10, 10, 10]} />
          {!clicked && <Earth clicked={clicked} />}
          <OrbitControls enablePan={false} enableZoom={false} enableDamping dampingFactor={0.25} rotateSpeed={0.3} />
        </Canvas>
      )}
      {!showNewWindow && (
        <div className={`overlay ${clicked ? 'clicked' : ''}`}>
          <div className="text">
            <span className="small">Diving</span>
            <span className="large">ES</span>
          </div>
        </div>
      )}
    </div>
  );
};

export default App;
