import './style/App.css'
import React, { useState, useRef } from 'react';
import { Canvas } from '@react-three/fiber';
import { OrbitControls } from '@react-three/drei';
import { useSpring, animated } from 'react-spring';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import Earth from './components/Earth';
import Home from './pages/Home';

const App = () => {
  const [clicked, setClicked] = useState(false);
  const [animation, setAnimation] = useState({ scale: 1, position: [0, 0, 2] });

  const handleClick = () => {
    setClicked(true);

    // Inicia la animación de escala y posición
    setAnimation({
      scale: 0,
      position: [0, 0, -2],
    });

    // Después de un tiempo, redirige a la nueva página
    setTimeout(() => {
      setClicked(false); // Resetea el estado para futuros clics
    }, 1000); // Ajusta el tiempo según tus necesidades
  };

  return (
    <Router>
      <div className={`app-container ${clicked ? 'clicked' : ''}`} onClick={handleClick}>
        <Canvas camera={{ position: animation.position }}>
          <ambientLight />
          <pointLight position={[10, 10, 10]} />
          {!clicked && <Earth />}
          <OrbitControls enablePan={false} enableZoom={false} enableDamping dampingFactor={0.25} rotateSpeed={0.3} />
        </Canvas>
        <animated.div className={`overlay ${clicked ? 'clicked' : ''}`} style={{ scale: animation.scale }}>
          <div className="text">
            <span className="small">Diving</span>
            <span className="large">ES</span>
          </div>
        </animated.div>
      </div>
      <Routes>
        <Route path="/home" element={<Home />} />
        {/* Agregamos una ruta de redirección para mantener la transición */}
        {clicked && <Route path="/" element={<Navigate to="/home" />} />}
      </Routes>
    </Router>
  );
};

export default App;
