import './style/App.css';
import React, { useState } from 'react';
import { Canvas } from '@react-three/fiber';
import { OrbitControls } from '@react-three/drei';
import { useSpring, animated } from 'react-spring';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import Earth from './components/Earth';
import Home from './pages/Home';

const App = () => {
  const [clicked, setClicked] = useState(false);
  const [redirect, setRedirect] = useState(false);

  const animationProps = useSpring({
    scale: clicked ? 0 : 1,
    position: clicked ? [0, 0, -2] : [0, 0, 2],
    onRest: () => {
      if (clicked) {
        // Después de que la animación de escala y posición termine,
        // establece la redirección a true
        setRedirect(true);
      }
    },
  });

  const handleClick = () => {
    setClicked(true);
  };

  return (
    <Router>
      <div className={`app-container`} onClick={handleClick}>
        <Canvas camera={{ position: animationProps.position }}>
          <ambientLight />
          <pointLight position={[10, 10, 10]} />
          {!clicked && <Earth />}
          <OrbitControls enablePan={false} enableZoom={false} enableDamping dampingFactor={0.25} rotateSpeed={0.3} />
        </Canvas>
        <animated.div className={`overlay`} style={{ scale: animationProps.scale }}>
          <div className="text">
            <span className="small">Diving</span>
            <span className="large">ES</span>
          </div>
        </animated.div>

        {/* Si la redirección está activa, navega a la ruta "/home" */}
        {redirect && <Navigate to="/home" />}
      </div>
      <Routes>
        <Route path="/home" element={<Home />} />
      </Routes>
    </Router>
  );
};

export default App;
