import './style/App.css';
import React, { useState } from 'react';
import { Canvas } from '@react-three/fiber';
import { OrbitControls } from '@react-three/drei';
import { BrowserRouter as Router, Routes, Route, Link, Navigate } from 'react-router-dom';
import Earth from './components/Earth';
import Home from './pages/Home';

const MainPage = () => {
  const [showLink, setShowLink] = useState(false);

  const handleTextClick = () => {
    // Al hacer clic en el texto, muestra el enlace
    setShowLink(true);
  };

  return (
    <div className="app-container">
      <Canvas camera={{ position: [0, 0, 2] }}>
        <ambientLight />
        <pointLight position={[10, 10, 10]} />
        <Earth />
        <OrbitControls enablePan={false} enableZoom={false} enableDamping dampingFactor={0.25} rotateSpeed={0.3} />
      </Canvas>
      <div className="overlay">
        <div className="text" onClick={handleTextClick}>
          <span className="small">Diving</span>
          <span className="large">ES</span>
        </div>
        {showLink && <Link to="/home">Ir a la nueva ventana</Link>}
      </div>
    </div>
  );
};

const App = () => {
  return (
    <Router>
      <Routes>
        <Route path="/" element={<MainPage />} />
        <Route path="/home" element={<Home />} />
      </Routes>
    </Router>
  );
};

export default App;
